  html_content = '<div id=\"lesson-natures-wonders-content\"><div id=\"falling-elements-container-nature\"></div><h1>Nature''s Wonders: Cycles of Life</h1><p>Explore nature''s beauty and its cycles.</p><img src=\"placeholder_nature.png\" alt=\"Nature Cycle\"></div>',
