  javascript_content = 'document.addEventListener(''DOMContentLoaded'', function() { const c = document.getElementById(''lesson-natures-wonders-content''); if(!c) return; const ec = c.querySelector(''#falling-elements-container-nature''); if (!ec) return; const et = [''🍂'', ''🍁'', ''🌸'', ''🌿'']; let ei; function cf() { if (!document.body.contains(c) || getComputedStyle(c).display === ''none'') { clearInterval(ei); return; } const el = document.createElement(''div''); el.className = ''natural-element''; el.textContent = et[Math.floor(Math.random() * et.length)]; el.style.left = Math.random() * 95 + ''%''; el.style.fontSize = Math.random() * 8 + 12 + ''px''; el.style.animationDuration = Math.random() * 4 + 6 + ''s''; ec.appendChild(el); el.classList.add(''animate''); el.addEventListener(''animationend'', function() { el.remove(); }); } function sa() { if (document.body.contains(c) && getComputedStyle(c).display !== ''none'') { ei = setInterval(cf, 800); } } function xa() { clearInterval(ei); } const o = new IntersectionObserver(e => e.forEach(x => x.isIntersecting ? sa() : xa()), { threshold: 0.1 }); if(c) o.observe(c); else xa(); const mo = new MutationObserver((m, ob) => { if (!document.body.contains(c)) { xa(); ob.disconnect(); } }); mo.observe(document.body, { childList: true, subtree: true }); });'
WHERE title = 'Nature''s Wonders';

