-- Lesson 4: The Magic of Colors
UPDATE lessons
SET
  html_content = '<div id=\"lesson-magic-of-colors-content\"><h1>The Magic of Colors</h1><p>Discover primary, secondary, and tertiary colors!</p><div class=\"color-palette\"><div class=\"color-box primary\" style=\"background-color:red;\">Red (P)</div><div class=\"color-box primary\" style=\"background-color:yellow; color:black;\">Yellow (P)</div><div class=\"color-box primary\" style=\"background-color:blue;\">Blue (P)</div><div class=\"color-box secondary\" style=\"background-color:orange;\">Orange (S)</div><div class=\"color-box secondary\" style=\"background-color:green;\">Green (S)</div><div class=\"color-box secondary\" style=\"background-color:purple;\">Purple (S)</div></div><button id=\"color-changer-button\">Change Palette Background</button></div>',
  css_content = '#lesson-magic-of-colors-content { font-family: Arial, sans-serif; padding: 20px; text-align:center; border-radius:10px; background-color:#f0f8ff;} #lesson-magic-of-colors-content h1 { color: #333; } #lesson-magic-of-colors-content .color-palette { display: flex; flex-wrap:wrap; justify-content: center; padding: 10px; margin: 15px 0; border-radius:5px; transition: background-color 0.5s ease;} #lesson-magic-of-colors-content .color-box { width: 100px; height: 70px; margin: 5px; display: flex; align-items: center; justify-content: center; font-weight: bold; color: white; border-radius: 5px; text-shadow: 1px 1px 1px #555;} #lesson-magic-of-colors-content button { padding: 10px 15px; margin-top:10px; border:none; background-color:#ff69b4; color:white; border-radius:5px; cursor:pointer; }',
  javascript_content = 'document.addEventListener(''DOMContentLoaded'', function() { const l = document.getElementById(''lesson-magic-of-colors-content''); if(!l) return; const b = l.querySelector(''#color-changer-button''); const p = l.querySelector(''.color-palette''); const c = [''#fff0f5'', ''#e6e6fa'', ''#f0fff0'', ''#fffacd'', ''#f5f5dc'']; let ci = 0; if(b && p) { b.addEventListener(''click'', function() { ci = (ci + 1) % c.length; p.style.backgroundColor = c[ci]; }); } });'
